import React, { useEffect, useState } from "react";

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loginError, setLoginError] = useState(false);
  const [loginMessage, setLoginMessage] = useState("");

  function checkFormIsFilled() {
    let formIsFilled = true;
    if (username === "") {
      setLoginMessage("Username cannot be empty");
      formIsFilled = false;
    } else if (password === "") {
      setLoginMessage("Password cannot be empty");
      formIsFilled = false;
    }
    return formIsFilled;
  }

  function handleLogin() {
    const formIsFilled = checkFormIsFilled();
    if (formIsFilled) {
      // validate sign in info on backend and sign them in
      //else show invalid credentials
    }
    console.log("Loggin in ", username, password);
  }

  return (
    <div>
      <h2>Login Page</h2>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      {loginError && <p>Invalid username or password. Please try again.</p>}

      <button onClick={handleLogin}>Login</button>
    </div>
  );
}

export default Login;
