function ReflectionTextEntry({ submitReflection }) {
  return (
    <>
      <textarea type="text" rows="5" className="reflectionTextEntry"></textarea>

      <button
        type="submit"
        className="reflectionSubmitButton"
        onClick={() => submitReflection(textArea)}
      >
        Submit
      </button>
    </>
  );
}

export default ReflectionTextEntry;
